const
    { spawn } = require('child_process'),
    path      = require('path'),
    pkg       = '@bryntum/calendar-lib',
    script    = `${pkg}/build.js`;
try {
    spawn('node', [require.resolve(script)], { cwd : process.cwd(), stdio : 'inherit' });
    spawn('node', [require.resolve(script)], { cwd : path.join(process.cwd(), 'source/lib/Core/helper'), stdio : 'inherit' });
}
catch (e) {
    console.log(`Optional ${pkg} is not installed`);
}
