import Override from '../../../Core/mixin/Override.js';
import GlobalEvents from '../../../Core/GlobalEvents.js';

class GlobalEventsOverride {
    static get target() {
        return {
            class: GlobalEvents,
        };
    }

    static setupFocusListenersOnce(rootElement, EventHelper) {
        // Shouldn't attach global focus listeners to the body in Salesforce

        if (rootElement !== document.body) {
            this._overridden.setupFocusListenersOnce.apply(this, arguments);
        }
    }
}

Override.apply(GlobalEventsOverride);
